import React, { useState } from 'react';
import {
  Box
} from '@aragon/ui';
import BigNumber from 'bignumber.js';
import {
  BalanceBlock, MaxButton,
} from '../common/index';
import {approve, stake} from '../../utils/web3';
import {isPos, toBaseUnitBN} from '../../utils/number';
import {UNI} from "../../constants/tokens";
import BigNumberInput from "../common/BigNumberInput";
import ActionButton from "../common/ActionButton";
import colors from '../../constants/colors';

type StakeProps = {
  user: string,
  poolAddress: string,
  balance: BigNumber,
  allowance: BigNumber,
  staked: BigNumber
};

function Stake({
  user, poolAddress, 
  staked, balance, allowance
}: StakeProps) {
  const [stakeAmount, setStakeAmount] = useState(new BigNumber(0));

  return (
    <Box heading="Stake">
      <div style={{display: 'flex', flexWrap: 'wrap'}}>
        <div style={{flexBasis: '32%'}}>
          <BalanceBlock asset="Staked" balance={staked} suffix={"UNI-V2"} type={"row"}/>
        </div>
        <div style={{flexBasis: '33%', paddingTop: '2%'}}>
          <div style={{display: 'flex'}}>
            <div style={{width: '60%', minWidth: '6em'}}>
              <>
                <BigNumberInput
                  adornment="UNI-V2"
                  value={stakeAmount}
                  setter={setStakeAmount}
                />
                <MaxButton
                  onClick={() => {
                    setStakeAmount(balance);
                  }}
                />
              </>
            </div>
            {allowance.comparedTo(stakeAmount) > 0 ?
            <div style={{width: '40%', minWidth: '7em'}}>
              <ActionButton 
                label={"STAKE"} 
                color={colors.button} 
                onClick={() => {
                  stake(
                    poolAddress,
                    toBaseUnitBN(stakeAmount, UNI.decimals),
                    (hash) => setStakeAmount(new BigNumber(0))
                  );
                }}
                disabled={poolAddress === '' || user === '' || !isPos(stakeAmount) || stakeAmount.isGreaterThan(balance)}
              />
            </div>
            : 
            <div style={{width: '40%', minWidth: '7em'}}>
              <ActionButton 
                label={"APPROVE"} 
                color={colors.button} 
                onClick={() => {
                  approve(UNI.addr, poolAddress);
                }}
                disabled={poolAddress === '' || user === ''}
              />
            </div>
            }
          </div>
        </div>
        <div style={{flexBasis: '2%'}}/>
      </div>
    </Box>
  );
}

export default Stake;
