import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';

import { Bar, BackButton, LinkBase } from '@aragon/ui';
import ConnectButton from './ConnectButton';
import ChangeModeButton from './SwitchTheme';

type NavbarProps = {
  theme:string,
  updateTheme: Function,
  user: string,
  setUser: Function
}

function NavBar({
  theme, updateTheme, user, setUser,
}:NavbarProps) {
  const history = useHistory();
  const [isHome, updateIsHome] = useState(true);
  const [page, setPage] = useState("");

  useEffect(() => {
    const home = history.location.pathname === '/';
    updateIsHome(home);
    return history.listen((location) => {
      setPage(location.pathname)
      const home = history.location.pathname === '/';
      updateIsHome(home);
    })
  }, [user, history]);

  return (
    <Bar
      primary={
        isHome ? (
          <></>
        ) : (
          <>
            <LinkButton
              title="Distribution"
              onClick={() => {
                history.push('/distribution/');
              }}
              isSelected={page.includes('/distribution')}
            />
            <LinkButton
              title="Pools"
              onClick={() => {
                history.push('/pools/');
              }}
              isSelected={page.includes('/pools')}
            />
            <LinkButton
              title="Admin (TESTING ONLY)"
              onClick={() => {
                history.push('/admin/');
              }}
              isSelected={page.includes('/admin')}
            />
          </>
        )
      }
      secondary={(
        <>
          <ConnectButton user={user} setUser={setUser} />

        </>
      )}
    />
  );
}


type linkButtonProps = {
  title:string,
  onClick: Function,
  isSelected?:boolean
}

function LinkButton({ title, onClick, isSelected = false }:linkButtonProps) {
  return (
    <div style={{ paddingLeft: 40 }}>
      <LinkBase onClick={onClick}>
        <div style={{ padding: '1%', opacity: isSelected ? 1 : 0.5, fontSize: 17 }}>{title}</div>
      </LinkBase>
    </div>
  );
}

export default NavBar;
