import React, { useState } from 'react';
import {
  Box
} from '@aragon/ui';
import BigNumber from 'bignumber.js';
import {
  BalanceBlock, MaxButton,
} from '../common/index';
import {unstake} from '../../utils/web3';
import {isPos, toBaseUnitBN} from '../../utils/number';
import {UNI} from "../../constants/tokens";
import BigNumberInput from "../common/BigNumberInput";
import ActionButton from "../common/ActionButton";
import colors from '../../constants/colors';

type UnstakeProps = {
  user: string,
  poolAddress: string,
  unstakable: BigNumber,
  locked: BigNumber
};

function Unstake({
  user, poolAddress, unstakable, locked
}: UnstakeProps) {
  const [unstakeAmount, setUnstakeAmount] = useState(new BigNumber(0));

  return (
    <Box heading="Unstake">
      <div style={{display: 'flex', flexWrap: 'wrap'}}>
        <div style={{width: '32%'}}>
          <BalanceBlock asset="Locked" balance={locked} suffix={"UNI-V2"} type={"row"}/>
          <BalanceBlock asset="Available" balance={unstakable} suffix={"UNI-V2"} type={"row"}/>
        </div>
        <div style={{flexBasis: '33%', paddingTop: '2%'}}>
          <div style={{display: 'flex'}}>
            <div style={{width: '60%', minWidth: '6em'}}>
              <>
                <BigNumberInput
                  adornment="UNI-V2"
                  value={unstakeAmount}
                  setter={setUnstakeAmount}
                />
                <MaxButton
                  onClick={() => {
                    setUnstakeAmount(unstakable);
                  }}
                />
              </>
            </div>
            <div style={{width: '40%'}}>
              <ActionButton 
                label={"UNSTAKE"} 
                color={colors.button} 
                onClick={() => {
                  unstake(
                    poolAddress,
                    toBaseUnitBN(unstakeAmount, UNI.decimals),
                    (hash) => setUnstakeAmount(new BigNumber(0))
                  );
                }} 
                disabled={poolAddress === '' || user === '' || !isPos(unstakeAmount) || unstakeAmount.isGreaterThan(unstakable)}
              />
            </div>
          </div>
        </div>
      </div>
    </Box>
  );
}

export default Unstake;
